sys_script_include.script:
var DebugLog = Class.create();
DebugLog.prototype = {
	pefix: '',
	cached: [],
    initialize: function() {
    },
	log: function(message) {
		gs.info(this.prefix + ': ' + message);
	},
	show: function(object, message) {
		var property;
		var finalMessage = this.prefix + ' --> ' + message + ': \n';
		for(property in object) {
			if (object[property] != null && !this._isFunction(object,property)) {
				finalMessage += '***' + property + ': ' + object[property] + '\n';	
			} else if (object[property] != null) {
				finalMessage += '***' + property + ': ' + JSON.stringify(object[property]);
			}
		}
		gs.info(finalMessage);
	},
	sc: function(name) {
		if (this.cached[name+'']) {
			var cachedValue = this.cached[name+''];
			this.show(cachedValue.object, cachedValue.message);
		}
	},
	setPrefix: function(message) {
		this.prefix = message;
	},
	addCached: function(name, object, message) {
		this.cached[name] = {'object': object, 'message': message};
	},
	_isFunction: function(object, property) {
		return typeof object[property] === 'function';
	},

    type: 'DebugLog'
};
