sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getEmail: function() {
		var userID = this.getParameter('sysparm_userid');
		var gr = new GlideRecord('sys_user');
		gr.get(userID);
		var result = this.newItem('result');
		//var text = this.getDocument().createTextNode("text");
		//var otro = this.getDocument().createElement('otro');
		//otro.appendChild(textNode);
		//result.appendChild(otro);
		result.setAttribute('value', gr.email);
	},
	_addItem : function(itemName, attName, attValue, html) {
		var item = this.newItem(itemName);
		item.setAttribute(attName, attValue);
	},
	type: 'GetEmailAddress'
});
