sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var dl = new DebugLog();
	dl.setPrefix('Testing REST resource ni_getinfo');
	dl.addCached('rec',request,'Request Object');
	dl.addCached('res',response,'Response Object');
	var body = {};
	try {
		var userName = request.pathParams.user_name;
		var niQuery = request.queryParams.ni_query;
		var user = new GlideRecord('sys_user');
		user.get('user_name',userName);
		var userId = user.sys_id + '';
		//dl.sc('rec');
		//dl.show(this,'this');

		var gr = new GlideAggregate('x_58872_needit_needit');
		gr.addAggregate('COUNT');
		gr.addQuery('u_requested_for',user.sys_id);
		gr.addEncodedQuery(niQuery);
		gr.groupBy('u_requested_type');
		gr.query();
		dl.sc('rec');

		var requestsByType = [];
		var totalCount = 0;
		while (gr.next()) {
			var typeCount = gr.getAggregate('COUNT');
			var requestType = gr.u_requested_type.getDisplayValue();
			requestsByType.push({'request_type': requestType, 'count': typeCount});
			totalCount += parseInt(typeCount);
		}

		body.total_requests = totalCount;
		body.requests_count = requestsByType;
		body.user = {'user_name': userName, 'user_id': userId};
	} catch(err) {
		var errorData = {};
		errorData.message = err.message;
		errorData.detail = err.name;
		body.error = errorData;
		body.status = 'failure';
	}
	var writer = response.getStreamWriter();
	response.setStatus(200);
	response.setContentType('application/json;charset=UTF-8');
	//response.setBody(body);
	var trace= '';
	var tracer = function (key, value){
		trace +=('--Key: '+ key);
		trace +=('--Value: '+ value);
		trace += (typeof value);
		return value;
	};

	dl.log(JSON.stringify(body,tracer,'x'));
	dl.log(trace);
	writer.writeString(JSON.stringify(body,tracer,''));
})(request, response);
